sys_script_include.script:
var UpdateFieldProcessor = Class.create();
UpdateFieldProcessor.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    process: function() {
        var sysId = this.getParameter('sysparm_sys_id');
        var fieldName = this.getParameter('sysparm_field_name');

        // 获取目标记录并更新字段
        var record = new GlideRecord('x_206101_new_hir_0_equipment_request');
        if (record.get(sysId)) {
            record.setValue('comment', fieldName); // 替换新值
            record.update(); // 更新记录
            return 'success'; // 返回成功
        }
        return 'failure'; // 如果没有找到记录，返回失败
    },

    type: 'UpdateFieldProcessor'
});
