sys_ui_page.client_script:
function closeWindow() {
    GlideModal.get().destroy();
}

/**
 * 创建一个 GlideAjax 请求，用于调用 Script Include
 */
function updateField() {
    // 获取当前表单的sys_id
    var sysId = g_form.getUniqueValue();

    // 创建GlideAjax请求
    var ga = new GlideAjax("x_206101_new_hir_0.UpdateFieldProcessor");
    ga.addParam('sysparm_sys_id', sysId);
    ga.addParam('sysparm_field_name', g_form.getValue('comment'));
    ga.getXML(function(response) {
        var answer = response.responseXML.documentElement.getAttribute('answer');
        if (answer == 'success') {
            // 更新成功，刷新表单中的comment字段
			g_form.save();
            g_form.showFieldMsg('comment', 'Comment updated successfully', 'info');
        } else {
            g_form.showFieldMsg('comment', 'Failed to update comment', 'error');
        }
        g_form.clearValue('stock');
        g_form.setValue('stock', answer);
        GlideModal.get().destroy();
    });
}
